adm.htmlad.on( 'init', function() {
	
	
	if(extParams.local==1){ adm.htmlad.defaultStyles = {}; }
	
	
	adm.cs.isFallback = false;
	adm.cs.isT3 = false;
	adm.cs.isT2 = false;
	adm.cs.isValidZip = false;
	adm.cs.isStatic = false;
	adm.cs.isDealerFallback = false;
	adm.cs.isInvFallback = false;
	adm.cs.hasInteractionOccurred = false;
	adm.cs.isOnEndFrame = false;
	adm.cs.currentInventoryIndex = 0;
	adm.cs.firstFrame = true;
	
	
	adm.cs.totalInventoryItems = 5;
	
    adm.cs.adSize = adm.$('template').attributes['data-adsize'].value;
		
});



adm.htmlad.on( 'dealer_fallback', function() {

	
	adm.$('template').replaceClass('ready', 'static');
	adm.cs.isFallback = true;
	adm.cs.isDealerFallback = true;
	adm.cs.isStatic = true;
	
	
	adm.htmlad.param('txt_preroll', 'Visit Your Local Buick Dealership');
	adm.htmlad.param('txt_title', '');
	adm.htmlad.param('txt_link_custom', 'Find a Dealer');
	adm.htmlad.param('txt_has_disclaimer', 'No');
	
	
	if (adm.cs.adSize == 'ct' || adm.cs.adSize == 'sw' || adm.cs.adSize == 'sn') { 
		adm.htmlad.param('txt_title', 'Visit Your Local Buick Dealership');
		return;
	}
	
	adm.$('.scroll_arrow.prev').addClass('hideMe');
	adm.$('.scroll_arrow.next').addClass('hideMe');

});



adm.htmlad.on('inv_fallback', function() {
	
	
	adm.cs.isFallback = true;
	adm.cs.isInvFallback = true;
	
	
	if (adm.cs.adSize == 'ct' || adm.cs.adSize == 'sw' || adm.cs.adSize == 'sn') { return; }
	
	
	adm.$('.scroll_arrow.prev').addClass('hideMe');
	adm.$('.scroll_arrow.next').addClass('hideMe');
	
});



adm.htmlad.on('pre_search', function(api_params) { 
   if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws' || adm.cs.adSize == 'ss') { 
      adm.htmlad.param('inv_orientation', 'vert'); 
      api_params.maxadnails = 8;  
   } else if (adm.cs.adSize == 'bb') { 
      api_params.maxadnails = 9; 
   } else {
      api_params.maxadnails = 5; 
   }
}); 



adm.htmlad.on( 'pre_image', function() {

	
	if (adm.cs.adSize == 'ct' || adm.cs.adSize == 'sw' || adm.cs.adSize == 'sn') {
		adm.htmlad.args.features = { dap: 1, r_zip: 1, t3: 1 }
		return;
	}

	if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws') { 
		adm.cs.totalInventoryItems = 4;  
	} else if (adm.cs.adSize == 'bb') { 
		adm.cs.totalInventoryItems = 3;
	} else {
		adm.cs.totalInventoryItems = 5;
	}
   
	
	
	
	var disclaimerVal;
	disclaimerVal = adm.htmlad.param('txt_has_disclaimer').toLowerCase();
	if(disclaimerVal == 'no') {
		adm.$('legalButton').addClass('hideMe');
	}

});



adm.htmlad.on( 'complete', function() {
	
	
	if(document.body.className.indexOf('animation') < 0) {
		adm.$('template').addClass('static');
		adm.cs.isStatic = true;
	}
	
	
	adm.$('template').replaceClass('loading', 'ready');
	
	
	adm.$('overlay').onmouseup = function(e) {
		if(adm.isRightClick(e)){ return false; }
		if(adm.htmlad.param('txt_link_override') !== '') {
			adm.$('#txt_link').setAttribute('admhref', adm.htmlad.param('txt_link_override'));
		}
		adm.htmlad.click('txt_link');
	};
	
	
	adm.$('cta').onmouseup = function(e) {
		if(adm.isRightClick(e)){ return false; }
		if(adm.htmlad.param('txt_link_override') !== '') {
			adm.$('#txt_link').setAttribute('admhref', adm.htmlad.param('txt_link_override'));
		}
		adm.htmlad.click('txt_link');
	};

	adm.cs.fontScaling();
	
	
	if (adm.cs.adSize == 'ct' || adm.cs.adSize == 'sw' || adm.cs.adSize == 'sn') { return; }

	
	
	adm.$('legalButton').onmouseup = function(e) {
		adm.$('legalPopup').show();
		if(adm.cs.firstFrame) {
			adm.cs.pauseTimeout();
		}
	};
	adm.$('closeButton').onmouseup = function(e) {
		adm.$('legalPopup').hide();
		if(adm.cs.firstFrame) {
			adm.cs.firstFrame = false;
			adm.cs.resumeTimeout();
		}
	};

	
	adm.$('legalPopup').hide();
	if (adm.htmlad.param('animation') == 0) {
		
		adm.$('legalButton').hide();
	}
	
	
	adm.$('.scroll_arrow.prev').onmouseup = function() {
		adm.cs.hasInteractionOccurred = true;
		if (!adm.cs.isOnEndFrame) {
			
			if (adm.cs.currentInventoryIndex > 0) {
				adm.cs.currentInventoryIndex--;
				adm.cs.prevSlide();
				
				if (adm.cs.currentInventoryIndex <= 0) {
					adm.cs.currentInventoryIndex = 0;
					adm.cs.disablePrevArrow();
				}
			}
		}
	};
	
	
	adm.$('.scroll_arrow.next').onmouseup = function() {
		adm.cs.hasInteractionOccurred = true;
		if (!adm.cs.isOnEndFrame) {
			
			if (adm.cs.currentInventoryIndex < adm.cs.totalInventoryItems - 1) {
				adm.cs.currentInventoryIndex++;
				adm.cs.nextSlide();
				adm.cs.enablePrevArrow();
			
			} else {
				adm.cs.showEndFrame();
			}
		}
	};
	
	
	adm.$('.scroll_arrow.back').onmouseup = function() {
		adm.cs.hideEndFrame();
	};

	
	adm.$('endFrameOverlay').onmouseup = function(e) {
		if(adm.isRightClick(e)){ return false; }
		if(adm.htmlad.param('txt_link_override') !== '') {
			adm.$('#txt_link').setAttribute('admhref', adm.htmlad.param('txt_link_override'));
		}
		adm.htmlad.click('txt_link');
	};
	
	
	if (adm.cs.isFallback) {
		
		adm.$('template').replaceClass('ready','fallback');
		adm.cs.showEndFrameStatic();
		adm.cs.hideBackButton();
	} else {
		
		adm.$('inv_container').hide();
		if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws' || adm.cs.adSize == 'bb') { adm.$('inv_container_outer').hide(); }
		adm.cs.hideBackButton();
		if (adm.htmlad.cssAnimationSupport) {
			adm.cs.startTimer();
		}
		if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ss'){
			adm.$('txt_title').addClass('titleWhite');
		}
	}
	
});



adm.htmlad.on('invclick', function(htmlad, div) { 
	
	if(adm.htmlad.param('txt_link_override') !== '') {
		div.setAttribute('admhref', adm.htmlad.param('txt_link_override'));
	}
	adm.htmlad.click(div);
});



adm.htmlad.on( 'click', function(e) {
	return (!adm.isRightClick(e));
});



