adm.cs.enablePrevArrow = function() {
	adm.$('.scroll_arrow.prev').show();
};


adm.cs.disablePrevArrow = function() {
	adm.$('.scroll_arrow.prev').hide();
};


adm.cs.showPrevArrow = function() {
	adm.$('.scroll_arrow.prev').show();
	adm.$('.scroll_arrow.next').show();
};


adm.cs.hidePrevArrow = function() {
	adm.$('.scroll_arrow.prev').hide();
	adm.$('.scroll_arrow.next').hide();
};


adm.cs.showBackButton = function() {
	adm.$('.scroll_arrow.back').show();
};


adm.cs.hideBackButton = function() {
	adm.$('.scroll_arrow.back').hide();
};
 

adm.cs.nextSlide = function() {
	if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws') { 
		adm.htmlad.invAdvanceSlideShow(2); 
	} else if (adm.cs.adSize == 'bb') { 
		adm.htmlad.invAdvanceSlideShow(3);
	} else { 
		adm.htmlad.invAdvanceSlideShow(1); 
	}
};


adm.cs.prevSlide = function() {
	if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws') { 
		adm.htmlad.invAdvanceSlideShow(-2); 
	} else if (adm.cs.adSize == 'bb') { 
		adm.htmlad.invAdvanceSlideShow(-3);
	} else { 
		adm.htmlad.invAdvanceSlideShow(-1); 
	}
};


adm.cs.showEndFrame = function() {
	if (!adm.cs.isOnEndFrame) {
		adm.cs.isOnEndFrame = true;
		if (adm.$('template').hasClass('static')) {
			
			adm.cs.showEndFrameStatic();	
		} else {	
			
			adm.$('endFrame').show();
			adm.$('legalButton').addClass('slideIn');
			adm.$('template').replaceClass('ready','final');
			adm.cs.hideBackButton();
			adm.cs.hidePrevArrow();
			adm.cs.nextSlide();
			adm.cs.showBackButton();
		}
		if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ss'){
			adm.$('txt_title').addClass('titleWhite');
		}
	}
};


adm.cs.hideEndFrame = function() {
	if (adm.cs.isOnEndFrame) {
		adm.cs.isOnEndFrame = false;
		if (adm.$('template').hasClass('static')) {
			
			adm.$('endFrame').hide();
			adm.$('legalButton').addClass('slideOut');
		} else {	
			
			adm.$('template').replaceClass('final','ready');
			adm.cs.hideBackButton();
			adm.cs.prevSlide();
			adm.cs.showPrevArrow();
		}
		if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ss'){
			adm.$('txt_title').removeClass('titleWhite');
		}
	}
};


adm.cs.showEndFrameStatic = function() {
	adm.$('endFrame').show();
};


adm.cs.beginAutoScroll = function() {
	
};


adm.cs.doAutoScroll = function() {
	if (!adm.cs.hasInteractionOccurred) {
		
		if (adm.cs.currentInventoryIndex < adm.cs.totalInventoryItems - 1) {
			adm.cs.currentInventoryIndex++;
			adm.cs.nextSlide();
			adm.cs.enablePrevArrow();
			adm.cs.beginAutoScroll();
		
		} else {
			var numSlides = adm.cs.totalInventoryItems - 1;
			adm.cs.currentInventoryIndex = 0;
			adm.htmlad.invAdvanceSlideShow(-numSlides);
			adm.cs.disablePrevArrow();
		}
	}
};





adm.cs.t = null;

adm.cs.startTimeout = new Date();

adm.cs.timeLeft = 0;

adm.cs.timeDelay = 6000;


adm.cs.startTimer = function() {
	adm.cs.isOnEndFrame = true;
	
	adm.cs.startTimeout = new Date();
	
	adm.cs.t = setTimeout(adm.cs.showInventory, adm.cs.timeDelay);
}


adm.cs.pauseTimeout = function() {
	
	adm.cs.timeLeft = adm.cs.timeDelay;
	
	adm.cs.timeLeft -= new Date() - adm.cs.startTimeout;
	
	clearTimeout(adm.cs.t);
}


adm.cs.resumeTimeout = function() {
	
	if(!adm.cs.timeLeft) { adm.cs.timeLeft = adm.cs.timeDelay; }
	
	adm.cs.t = setTimeout(adm.cs.showInventory, adm.cs.timeLeft);
}
	

adm.cs.showInventory = function() {
	
	adm.$('inv_container').show();
	adm.$('inv_container').addClass('slideIn');
	adm.cs.firstFrame = false;
	clearTimeout(adm.cs.t);
	
	if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ws' || adm.cs.adSize == 'bb') { adm.$('inv_container_outer').show(); adm.$('inv_container_outer').addClass('slideIn'); }

	if (adm.cs.adSize == 'fs' || adm.cs.adSize == 'ss'){
		adm.$('txt_title').removeClass('titleWhite');
	}
	
	adm.$('endFrame').addClass('slideOut');
	adm.$('legalButton').addClass('slideOut');
	adm.cs.disablePrevArrow();
	adm.cs.isOnEndFrame = false;
	if (adm.htmlad.cssAnimationSupport) {
		adm.cs.beginAutoScroll();
	}		
};


adm.cs.findNumbersInString = function(string) { return string.match(/[.0-9]*\d/g); };
 

adm.cs.findLettersInString = function(string) { return string.match(/([^0-9^\u002E\u002D])+/g); };


adm.cs.fontScaling = function() {
    
    var elements = document.querySelectorAll('[id^="txt_"]:not(#txt_disclaimer),[id^="_txt_"],[id^="inv_custom"]')
    Array.prototype.forEach.call(elements, function(el, i){
        
        while(el.scrollHeight > el.offsetHeight || el.scrollWidth > el.offsetWidth) {
            
            var fontSize = el.style['fontSize'] || window.getComputedStyle(el)['fontSize'];
            el.style['fontSize'] = ((parseFloat(adm.cs.findNumbersInString(fontSize)[0]) - (.1)).toFixed(1) + adm.cs.findLettersInString(fontSize)[0]).replace('.0','')
        }
    });
};

